mapper.select:
select DISTINCT bp.*,bpb.name projectName,bpph.name phaseName,bpt.name typeName from bidding_project bp LEFT JOIN bidding_project_bulid bpb on bpb.id=bp.project_bulid_id LEFT JOIN bidding_doc_interpretation bdi on bdi.id=bp.doc_interpretation_id LEFT JOIN bidding_product_collection bpc on bpc.id=bp.product_collection_id LEFT JOIN bidding_strategy_analysis bsa on bsa.id=bp.strategy_analysis_id LEFT JOIN bidding_info_filling bif on bif.id=bp.info_filling_id LEFT JOIN bidding_official_notice bon on bon.id=bp.official_notice_id LEFT JOIN bidding_project_summary bps on bps.id=bp.project_summary_id LEFT JOIN bidding_project_product bpp on bpp.project_id=bp.id LEFT JOIN bidding_project_phase bpph on bpph.id=bp.project_phase_now LEFT JOIN bidding_project_type bpt on bpt.id =bp.type_id WHERE bp.user_id=#{userId}
and bp.status= #{status}
and bpb.name like concat('%',#{name},'%')
ORDER BY bp.id DESC

select DISTINCT bp.*,bpb.name projectName,bpph.name phaseName,bpt.name typeName from bidding_project bp LEFT JOIN bidding_project_bulid bpb on bpb.id=bp.project_bulid_id LEFT JOIN bidding_doc_interpretation bdi on bdi.id=bp.doc_interpretation_id LEFT JOIN bidding_product_collection bpc on bpc.id=bp.product_collection_id LEFT JOIN bidding_strategy_analysis bsa on bsa.id=bp.strategy_analysis_id LEFT JOIN bidding_info_filling bif on bif.id=bp.info_filling_id LEFT JOIN bidding_official_notice bon on bon.id=bp.official_notice_id LEFT JOIN bidding_project_summary bps on bps.id=bp.project_summary_id LEFT JOIN bidding_project_product bpp on bpp.project_id=bp.id LEFT JOIN bidding_project_phase bpph on bpph.id=bp.project_phase_now LEFT JOIN bidding_project_type bpt on bpt.id =bp.type_id where bp.delflag='0' and bp.status='1'
and bpb.province_id= #{proId}
and bpb.city_id like concat('%',#{cityId},'%')
and bpb.pro_label= #{proLabel}
and bp.type_id= #{typeId}
and bpb.name like concat('%',#{name},'%')
and replace(bpb.doc_public_time, '/', '') between #{startTime} and #{endTime}
ORDER BY bp.id DESC

select DISTINCT bp.*,bpb.name projectName,bpb.province_id proId,bpph.name phaseName,bpt.name typeName, bif.file_price_letter,bdi.quality_level qualityLevel from bidding_project bp LEFT JOIN bidding_project_bulid bpb on bpb.id=bp.project_bulid_id LEFT JOIN bidding_doc_interpretation bdi on bdi.id=bp.doc_interpretation_id LEFT JOIN bidding_product_collection bpc on bpc.id=bp.product_collection_id LEFT JOIN bidding_strategy_analysis bsa on bsa.id=bp.strategy_analysis_id LEFT JOIN bidding_info_filling bif on bif.id=bp.info_filling_id LEFT JOIN bidding_official_notice bon on bon.id=bp.official_notice_id LEFT JOIN bidding_project_summary bps on bps.id=bp.project_summary_id LEFT JOIN bidding_project_product bpp on bpp.project_id=bp.id LEFT JOIN bidding_project_phase bpph on bpph.id=bp.project_phase_now LEFT JOIN bidding_project_type bpt on bpt.id =bp.type_id where bp.delflag='0' and bp.status='1'
and bdi.quality_level= #{qualityLevel}
and bpb.province_id= #{proId}
and bpb.product_id like concat('%',#{productId},'%')
ORDER BY bp.id DESC
