mapper.select:
select bu1.name graParentName,bu1.id graParentId,bu2.id parentId, bu2.name parentName,bu3.name name,bu3.id userId,buf.id id,buf.status status from bidding_user_framework buf left join bidding_user bu1 on bu1.id=buf.gra_parent_id left join bidding_user bu2 on bu2.id=buf.parent_id left join bidding_user bu3 on bu3.id=buf.user_id where buf.delflag='0' and buf.gra_parent_id != '' and bu1.delflag='0' and bu2.delflag='0' and bu3.delflag='0'
and buf.user_id= #{userId}
and buf.parent_id= #{parentId}
and buf.gra_parent_id= #{graParentId}
